[
    {
        "identifier": "6112c84944b84294898d992a551c0876",
        "name": "Block IP Address",
        "skill-description": "This intent is about adding a specific IP address to the firewall's block list, preventing it from accessing the network.",
        "slot": {
            "2bcff13acae94ba4af8d91db74c57236": {
                "name": "firewall_type",
                "slot-description": "The type of firewall being used.",
                "required-values": [
                    "hardware",
                    "software",
                    "cloud-based"
                ]
            },
            "a0deea88f95d4ef0ac00cf2e1ab32255": {
                "name": "ip_address",
                "slot-description": "The IP address to be blocked."
            },
            "cf60b8e38fce45dba01c7f370a71c451": {
                "name": "block_duration",
                "slot-description": "The duration for which the IP address should be blocked."
            },
            "f41588be9d0242bc9aaf7f57222f0d0b": {
                "name": "reason",
                "slot-description": "The reason for blocking the IP address."
            }
        }
    },
    {
        "identifier": "d8d69d360826461d9f2ec1ffae66c624",
        "name": "Reset Firewall Settings",
        "skill-description": "This intent is about resetting the current firewall settings to their default values.",
        "slot": {
            "3f41651abc6e4f02ad56e2e24f5b710c": {
                "name": "firewall_type",
                "slot-description": "The type of firewall for which settings are being reset."
            },
            "259995d1d1e04187b51aaf309fff8158": {
                "name": "firewall_vendor",
                "slot-description": "The vendor of the firewall."
            },
            "37542b04d92c41c388e32b99b63ada64": {
                "name": "firewall_model",
                "slot-description": "The model of the firewall."
            }
        }
    },
    {
        "identifier": "df5bce38a5904bac861c86224bd77a3f",
        "name": "Configure Firewall",
        "skill-description": "This intent is about setting up and configuring the firewall to meet specific security needs.",
        "slot": {
            "5a9f5b9042984fbfa38b69ef764e94b4": {
                "name": "firewall_type",
                "slot-description": "The type of firewall to configure."
            },
            "a8fa32ef345543ce8dc6822c159f2b4d": {
                "name": "firewall_rule",
                "slot-description": "The specific rule to configure for the firewall."
            },
            "0d61a335ccf84a6e8d9b17685e3f96d2": {
                "name": "protocol",
                "slot-description": "The protocol to apply the firewall rule to."
            },
            "b91ca54992b54bd3ab85e3a051d245d5": {
                "name": "port_number",
                "slot-description": "The port number to apply the firewall rule to."
            },
            "65d998c46e0c4e8880a3d2693c0eadb0": {
                "name": "source_ip",
                "slot-description": "The source IP address to apply the firewall rule to.",
                "required-values": [
                    "192.168.1.1",
                    "10.0.0.1",
                    "2001:0db8:85a3:0000:0000:8a2e:0370:7334"
                ]
            },
            "a62bd34295aa4d67a0a529f0078b2bb2": {
                "name": "destination_ip",
                "slot-description": "The destination IP address to apply the firewall rule to."
            }
        }
    },
    {
        "identifier": "fac6cd1b78514bc78f27d99a869d5fdb",
        "name": "Unblock IP Address",
        "skill-description": "This intent is about removing a previously blocked IP address from the firewall's block list.",
        "slot": {
            "f750f34487cf463889dce2ee800a6a8b": {
                "name": "ip_address",
                "slot-description": "The IP address to unblock."
            },
            "9a73480699954efc8f991510720aef77": {
                "name": "firewall_name",
                "slot-description": "The name of the firewall."
            }
        }
    }
]
