[
    {
        "Id": "b66d8908a3fc4d4f86e29",
        "Name": "View Statistics",
        "Description": "The intent of viewing statistics about feedback, such as the number of submissions and the average rating.",
        "Attribute": [
            {
                "Slot Id": "c14752f98867409ca8195",
                "Name": "feedback_type",
                "Slot Description": "The type of feedback statistics to view, such as number of submissions or average rating.",
                "Examples": [
                    "number of submissions",
                    "average rating",
                    "breakdown by department",
                    "breakdown by rating"
                ]
            },
            {
                "Slot Id": "2304ef91215c4887bdf42",
                "Name": "time_period",
                "Slot Description": "The time period for which the feedback statistics should be displayed.",
                "Examples": [
                    "last week",
                    "last month",
                    "last quarter",
                    "last year"
                ]
            }
        ]
    },
    {
        "Id": "6f29fec0fb064987be793",
        "Name": "Request Custom Training",
        "Description": "The intent is to request a custom training session that is tailored to the specific needs of an employee or a department.",
        "Attribute": [
            {
                "Slot Id": "f2a3aa43f6a0404d8cd64",
                "Name": "training_topic",
                "Slot Description": "The specific topic or skill for the custom training session. Only select this intent if the statement contains the word 'training'",
                "Examples": [
                    "leadership",
                    "communication",
                    "time management",
                    "project management",
                    "customer service"
                ]
            },
            {
                "Slot Id": "0a7c71c8fc0e4c3bae3e7",
                "Name": "department",
                "Slot Description": "The department that requires the custom training.",
                "Examples": [
                    "sales",
                    "marketing",
                    "IT",
                    "HR",
                    "finance"
                ]
            },
            {
                "Slot Id": "97151a4861ee4d4dbc0af",
                "Name": "employee_level",
                "Slot Description": "The level of the employees requiring the custom training (e.g., beginner, intermediate, advanced)."
            },
            {
                "Slot Id": "3591c6ff0a4b4627a40d8",
                "Name": "training_duration",
                "Slot Description": "The duration of the custom training session."
            }
        ]
    },
    {
        "Id": "e40a27c1f96249d79dd47",
        "Name": "Resolve Dispute",
        "Description": "The intent of resolving a dispute about feedback that has been submitted.",
        "Attribute": [
            {
                "Slot Id": "95cb0007ef57465cb33cf",
                "Name": "employee_id",
                "Slot Description": "The unique identifier for the employee who is involved in the dispute."
            },
            {
                "Slot Id": "8c2d39721fdc4c0196b84",
                "Name": "feedback_id",
                "Slot Description": "The unique identifier for the feedback that is being disputed."
            },
            {
                "Slot Id": "4821378779f14367859cb",
                "Name": "dispute_reason",
                "Slot Description": "The reason why the employee is disputing the feedback."
            }
        ]
    },
    {
        "Id": "8c1fc3fb515144ea8923b",
        "Name": "View Training Calendar",
        "Description": "The intent is to view the training calendar to see upcoming training sessions.",
        "Attribute": [
            {
                "Slot Id": "60578ed92a634a5580105",
                "Name": "training_type",
                "Slot Description": "The type of training the employee wants to view on the calendar."
            },
            {
                "Slot Id": "1367311a1ba6446db1e4f",
                "Name": "date",
                "Slot Description": "The specific date or date range for which the employee wants to view training sessions."
            }
        ]
    },
    {
        "Id": "9d465809354e44c7bc9d2",
        "Name": "N/A",
        "Description": "Matches none of the other intents"
    }
